snippets.cpp:
#include <string>
#include <algorithm>

std::string reverseString(const std::string& input) {
    std::string reversed = input;
    std::reverse(reversed.begin(), reversed.end());
    return reversed;
}

// Usage:
reverseString("quicksnip"); // Returns: "pinskciuq"
